mapper.select:
SELECT a.*, u.id AS "createBy.id", u.name AS "createBy.name"
FROM sys_log a JOIN sys_user u ON u.id = a.create_by
WHERE a.create_time BETWEEN #{beginDate} AND #{endDate}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.create_by = #{createBy.id}
AND a.request_uri LIKE '%'||#{requestUri}||'%' '%'+#{requestUri}+'%' CONCAT('%', #{requestUri}, '%')
AND a.type = #{TYPE_EXCEPTION}
AND a.type = #{type}
ORDER BY ${page.orderBy}
ORDER BY a.create_time DESC
